import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { v4 as uuidv4 } from 'uuid';
import { addBook } from '../redux/books/books';

const BookForm = () => {
  const [author, setAuthor] = useState('');
  const [title, setTitle] = useState('');
  const [category, setCategory] = useState('');
  const [authorError, setAuthorError] = useState('');
  const [titleError, setTitleError] = useState('');
  const dispatch = useDispatch();
  const categories = ['Beauty', 'Movie'];
  const handleChangeAuthor = (e) => {
    setAuthor(e.target.value);
    setAuthorError('');
  };

  const handleChangeTitle = (e) => {
    setTitle(e.target.value);
    setTitleError('');
  };

  const handleChangeCategory = (e) => {
    setCategory(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    let error = false;
    if (title.trim() === '') {
      error = true;
      setTitleError('Please enter your title');
    }
    if (author.trim() === '') {
      error = true;
      setAuthorError('Please enter your author');
    }
    if (!error) {
      const formData = {
        id: uuidv4(), title, author, category,
      };
      dispatch(addBook(formData));
      setTitle('');
      setAuthor('');
      setCategory('');
      setTitleError('');
      setAuthorError('');
    }
  };
  return (
    <form className="addForm" onSubmit={submitBookToStore}>
      <label htmlFor="title">
        <input type="text" id="title" placeholder="Title" onChange={getTitle} value={title} required />
      </label>
      <label htmlFor="author">
        <input  type="text" id="author" placeholder="Author" onChange={getAuthor} value={author} required />
      </label>
      <label htmlFor="genre">
        <select className="bookSelect" id="genre">
          <option value="">Genre</option>
          <option value="Action">Action</option>
          <option value="Science-Fiction">Science-Fiction</option>
          <option value="Economy">Economy</option>
          <option value="Romance">Romance</option>
        </select>
      </label>
      <button type="button" className="add btn" onClick={submitBookToStore}>Add</button>
    </form>
  );
};
export default BookForm;
