import React from 'react';
import PropTypes from 'prop-types';
import { useDispatch } from 'react-redux';
import { removeBook } from '../redux/books/books';

const Book = ({ id, title, author, category }) => {
  console.log(id);
  const dispatch = useDispatch();
  const remove = () => dispatch(removeBook(id));

  return (
    <>
      <div className="bookContainer">
        <div className="titleContainer">
          <p className="genre">Action</p>
          <p className="bookTitle">{title}</p>
          <p className="bookAuthor">{author}</p>
          <p>{category}</p>
          <button type="button" className="remove" onClick={() =>{ remove(id) }}>Remove</button>
        </div>
        <div className="graphContainer">
          <div className="graph" />
          <div className="percentageContainer">
            <p className="percentage">64%</p>
            <p className="completed">Completed</p>
          </div>
        </div>
        <div className="progressContainer">
          <p>Current Chapter</p>
          <p>Chapter 17</p>
          <button type="button" className="progressBtn">Update Progress</button>
        </div>
      </div>
    </>
  );
}

Book.propTypes = {
  id: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  author: PropTypes.string.isRequired,
};

export default Book;
