import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { v4 as uuidv4 } from 'uuid';
import { addBook } from '../redux/books/books';

const BookForm = () => {
  const [author, setAuthor] = useState('');
  const [title, setTitle] = useState('');
  const [category, setCategory] = useState('');
  const [errorBook, setErrorBook] = useState('');
  const categories = ['Select Category',
  'Action',
  'Science-Fiction',
  'Ecology',
  'Gastronomy',
  'Anime',];
  const dispatch = useDispatch();

  const getAuthor = (e) => {
    setAuthor(e.target.value);
  };

  const getTitle = (e) => {
    setTitle(e.target.value);
  };

  const getCategory = (e) => {
    setCategory(e.target.value);
  };

  const sumitBook = (e) => {
    e.preventDefault();
    if (title.trim() === '' || category.trim() === '') {
      setErrorBook('Please fill all fields');
    } else {
      const formData = {
        id: uuidv4(), title, author, category,
      };
      dispatch(addBook(formData));
      setTitle('');
      setAuthor('');
      setCategory('');
      setErrorBook('');
    }
  };
  return (
    <div className="div-form">
      <h2 className="title-book">Add New Book</h2>
      <form className="form" onSubmit={sumitBook}>
        <div className="input">
          <input
            type="text"
            placeholder="Enter title"
            id="title"
            onChange={getTitle}
            value={title}
            required
          />
          <span>
            {errorBook}
            {' '}
          </span>
        </div>
        <div className="input">
          <input
            type="text"
            placeholder="Enter author"
            id="author"
            onChange={getAuthor}
            value={author}
            required
          />
        </div>
        <select className="input" onChange={getCategory} value={category} >
          {
            categories.map((category) => (
              <option key={category} value={category} >{category}</option>
            ))
          }
        </select>
        <button type="button" className="addBtn input" onClick={sumitBook}>Add Book</button>

      </form>
    </div>
  );
};
export default BookForm;
